// Add to revealUtils.ts
export function revealAllMines(grid: Grid): Grid {
  return grid.map(row =>
    row.map(cell => {
      if (cell.hasMine && cell.status !== 'revealed') {
        return { ...cell, status: 'revealed' as const };
      }
      return cell;
    })
  );
}
